var id = $('#myId').val()
var Url = '../config/config.jp.php'
$(document).ready(function () {
    profil()
})

function profil() {
    $.ajax({
        url: Url,
        method: 'POST',
        dataType: 'JSON',
        data: { action: 'profil', id },
        success: function (data) {
            $('#profil').html(

                `
                <div class="card">
                    <div class="card-header">
                        <h3 class="text-center">Welcome Dear/${data.fullname}</h3>
                    </div>
                    <div class="card-body">
                    <table class="table tablesorter " id="plain-table">
                    <thead class=" text-primary">
                      <tr>
                        <th class="header">
                          FIELD
                        </th>
                        
                        <th class="header">
                          VALUE
                        </th>
                      </tr>
                    </thead>
                    <tbody>
                      <tr>
                        <td>Username: </td>
                        <td>${data.username}</td>
                      </tr>
                      <tr>
                        <td>fullname: </td>
                        <td>${data.fullname}</td>
                      </tr>
                      <tr>
                        <td>E-mail:</td>
                        <td>${data.email}</td>
                      </tr>
                      <tr>
                        <td>Gender:</td>
                        <td>${data.gender}</td>
                      </tr>
                      <tr>
                        <td>Status:</td>
                        <td>${data.status}</td>
                      </tr>
                      <tr>
                        <td>Partner:</td>
                        <td>${data.woman_name ? '' : 'No data'}</td>
                      </tr>
                      <tr>
                        <td>Number of kid:</td>
                        <td>${data.children}</td>
                      </tr>
                      <tr>
                        <td>Salary:</td>
                        <td>${data.salary}</td>
                      </tr>

                    </tbody>
                  </table>
                    </div>
                </div>
                `

            )
        }
    })
}